#calling on supply of token in ether format using address and abi
def supply_ERC20(contract_address, contract_abi):
    contract = web3_connection.eth.contract(address=contract_address, abi=contract_abi)
    supply_token = web3_connection.fromWei(contract.functions.totalSupply().call(), 'ether')


#calling on supply of bone in specific wallet using wallet address
def BONE_wallet_balance(BONE_address):
    BONE_address = Web3.toChecksumAddress(BONE_address)
    contract = web3_connection.eth.contract(address=SHIBASWAP_BONE_contract_address, abi=SHIBASWAP_BONE_contract_abi)
    balance_token = contract.functions.balanceOf(BONE_address).call()

VIBE = (BONE_wallet_balance(xc95_BONE_address))
