def BONE_circulating_supply():
    minted_supply = int(supply_ERC20(SHIBASWAP_BONE_contract_address,SHIBASWAP_BONE_contract_abi))
    staked_supply = int(supply_ERC20(t_BONE_contract_address,t_BONE_contract_abi))
    locked_supply = int(BONE_wallet_balance(unwoofed_BONE_address))
    SSLP_supply = int(BONE_wallet_balance(xc95_BONE_address)) + int(BONE_wallet_balance(xf6B_BONE_address)) + int(BONE_wallet_balance(x4bb_BONE_address)) + int(BONE_wallet_balance(x44c_BONE_address)) + int(BONE_wallet_balance(xefb_BONE_address)) + int(BONE_wallet_balance(xff6_BONE_address))
    circulating_supply = minted_supply - staked_supply - locked_supply - SSLP_supply
    print(circulating_supply)
